      <span style="color: #fff;font-size: 14px;letter-spacing: 0.1rem;">-</span>
      <span v-if="!loading" style="color: #fff;font-size: 14px;letter-spacing: 0.1rem;">{{ ntime }}</span>
    </div>
    <div class="nav-btn">
      <!-- <button style="color: white; padding: 10px; font-size: 16px; width: max-content" @click="wReload">&#10227;</button> -->
      <button :title="minimizeBtn[$i18n.locale.value]" @click="minimize">
        <svg
