<script>
import { ipcRenderer } from "electron";
export default {
  name: "Nav-bar",
  data() {
    return {
      ndate: null,
      minimizeBtn:{en:'Minimize',ru:"Свернуть"},
      closeBtn:{en:'Close',ru:"Закрыть"},
      ntime: null,
      loading: false,
    }
  },
  watch: {
    '$route': {
      immediate: true,
      handler() {
        var intervals = window.setInterval(function() {}, 0);
        while (intervals--) {
          window.clearInterval(intervals);
        }
        const _t = this
        setInterval(() => {
          _t.time()
        }, 1000);
      }
    },
  },
  methods: {
    time() {
      this.loading = true
      let date = new Date()
      let newdate = ('0'+date.getDate()).slice(-2)+'.'+('0'+(date.getMonth()+1)).slice(-2)+'.'+date.getFullYear()
      let newtime = ('0'+date.getHours()).slice(-2)+':'+('0'+date.getMinutes()).slice(-2)
      this.ntime = newtime
      this.ndate = newdate
      this.loading = false
    },
    close() {
      ipcRenderer.send("close");
    },
    minimize() {
      ipcRenderer.send("minimize");
    },
    wReload() {
      ipcRenderer.send("reload");
    },
  },
  mounted(){
    const _t = this
    _t.time()
    setInterval(() => {
      _t.time()
    }, 1000);
  }
};
</script>